mapper.select:
SELECT
	m.member_uid, i.inn_name, r.room_name, b.book_member_name, b.book_member_tel, b.book_date, b.book_member_cnt
FROM
	book b, member m, inn i, room r
WHERE
	b.member_uid = #{member_uid}
	and b.book_uid = #{book_uid}
	and b.room_uid = r.room_uid
	and r.inn_uid = i.inn_uid
LIMIT 1;
